mapper.select:
		SELECT 
            USER_ID AS userId,
            CREATE_DATE AS joinDate
        FROM 
            MEMBER
        WHERE 
            USER_NAME = #{userName}
            AND USER_EMAIL = #{userEmail}
            AND USER_ROLE = #{userRole}
            AND DELETE_YN = 'N'

mapper.update:
UPDATE MEMBER 
SET USER_PW = #{userPw}
WHERE
USER_ID = #{userId}
AND USER_NAME = #{userName}
AND USER_EMAIL = #{userEmail}
